mapper.delete:
delete from CBIMS_ORDER_BSYN_DETAIL

	order_info_id=#{orderInfoId}
	station_code=#{stationCode}
	user_id=#{userId}
	status=#{status}
	result_code=#{resultCode}

mapper.select:
select id, order_info_id as orderInfoId, station_code as stationCode, user_id as userId, status, result_code as resultCode, result_desc as resultDesc, create_time as createTime, update_time as updateTime, retry, version, serial_no as serialNo from CBIMS_ORDER_BSYN_DETAIL
order_info_id=#{orderInfoId} station_code=#{stationCode} user_id=#{userId} status=#{status} result_code=#{resultCode}
limit #{pageSize}
